goog.provide('DD.ui.renderer.ListContainer');goog.require('DD.ui.renderer.Component');/** * Рендерер для компонентов класса DD.ui.ListContainer. * @extends DD.ui.renderer.Component * @constructor * @this DD.ui.renderer.ListContainer */DD.ui.renderer.ListContainer = function(){	DD.ui.renderer.Component.call(this);};goog.inherits(DD.ui.renderer.ListContainer, DD.ui.renderer.Component);goog.addSingletonGetter(DD.ui.renderer.ListContainer);DD.ui.renderer.ListContainer.CSS_CLASS = 'DD-listcontainer';DD.ui.renderer.ListContainer.prototype.getCssClass = function(){	return DD.ui.renderer.ListContainer.CSS_CLASS;};/** * Устанавливает невозможность переноса элементов списка * @param {DD.ui.Component} component Компонент, к которому относится рендерер * @param {Boolean} enabled */DD.ui.renderer.ListContainer.prototype.setNowrap = function(component, enabled){	var el = component.Items.getElement();	if (!el)		return;	el.style.whiteSpace = enabled ? 'nowrap' : '';};