goog.require('DD.ui.renderer.Item');goog.provide('DD.ui.renderer.Field');/** * @constructor */DD.ui.renderer.Field = function() {	DD.ui.renderer.Item.call(this);};goog.inherits(DD.ui.renderer.Field, DD.ui.renderer.Item);goog.addSingletonGetter(DD.ui.renderer.Field);DD.ui.renderer.Field.CSS_CLASS = 'DD-field';DD.ui.renderer.Field.prototype.getCssClass = function(){	return DD.ui.renderer.Field.CSS_CLASS;};DD.ui.renderer.Field.prototype.createDom = function(component){	var element = DD.ui.renderer.Item.getInstance().createDom.call(this, component);	element.style.display = 'inline-block';	return element;};DD.ui.renderer.Field.prototype.getClassNames = function(component){	var classNames = DD.ui.renderer.Item.getInstance().getClassNames.call(this, component);	var sortState = component.getSortState();	if (sortState != DD.ui.Field.SortState.NONE)	{		var sortStateClass = sortState == DD.ui.Field.SortState.ASC ? 'asc' : 'desc';		classNames.push(this.getCssClass() + '-' + sortStateClass);	}	return classNames;};DD.ui.renderer.Field.prototype.initializeDom = function(component){	var content = component.getContentElement();	goog.events.listen(content, goog.events.EventType.CLICK, component.clickHandle, false, this);};DD.ui.renderer.Field.prototype.uninitializeDom = function(component){	var content = component.getContentElement();	goog.events.unlisten(content, goog.events.EventType.CLICK, component.clickHandle, false, this);};DD.ui.renderer.Field.prototype.setSortState = function(component, state){	var element = component.getElement();	var sortStateClass = sortState == DD.ui.Field.SortStates.ASC ? 'asc' : 'desc';	var baseClass = this.getCssClass();	goog.dom.classes.remove(element, [baseClass + '-asc', baseClass + '-desc']);	goog.dom.classes.add(element, baseClass + '-' + sortStateClass);};